<?php

namespace App\Http\Controllers;
use DB;
use Exception;
use Illuminate\Http\Request;
use App\Classes\Traits\General;

class GetAdminPermissionsController extends Controller
{
    use General;

	//image variable


	public function getAdminPermissionsPage()
    {
		try {

				$eventImage = "data:image/jpeg;base64,";

			//$this->writeToLog(print_r($event, true));
        	return view('permissionAdmin', ['eventimage' => $eventImage]);
		}

		catch(Exception $ex) {
			$error = $ex->getMessage();
			$this->writeToLog($error);
	
			$response = array(
				'status' => 'fail',
				'message' => $error
			);
	
			return $response;
		}
    }
}
